import React, { useState } from 'react';

const Form =()=> {


    const [data,setData]= useState({ 
        
    name:"",
    lastName:"",
    email:"",

});

const inputEvent=(event)=>{

const  {name,value}=event.target;

        setData((preVal) =>{

            return {

                ...preVal,

                [name]:value,





            };

        });

};

const onSubmit=(e)=>{
    e.preventDefault();

}


       





    /*
    const [name , setName]= useState("");

    const [lastName, setLastName]=useState("");

    const [fullName , setFullName]= useState("");
    const [fullLname,setfullLname]=useState("");



    const inputEvent =(event)=>{
        console.log(event.target.value);
        setName(event.target.value);
         
    }
    const inputEventTwo =(event)=>{
        console.log(event.target.value);
        setLastName(event.target.value);
    }

    const onSubmit = (event) =>{

        event.preventDefault();

        setFullName(name);
        setfullLname(lastName)


    }
    */

return(

        <>
             <div className="my-5">

<h3 className="text-center ">Hello <p className="text-center text-muted">Full Name : {data.name } {data.lastName}</p>
</h3>
    <p className="text-center text-success">Email : {data.email}</p>

</div>
    <div className="container contact_div" >
        <div className="row" >

            <div className="col-md-6 col-10 mx-auto" >

                                    
            <form  onSubmit={onSubmit}>
            <div class="form-group">
                <label for="exampleInputEmail1">Name</label>
                <input type="text" class="form-control" placeholder="Enter your name"
                    
                    onChange={inputEvent}
                    value={data.name}
                    name="name"
               
                />
              </div>
              <div class="form-group">
                <label for="exampleInputEmail1">Last Name</label>
                <input type="text" class="form-control" placeholder="Enter your Last name"
                    
                    onChange={inputEvent}
                    value={data.lastName}
                    name="lastName"
               
                />
              </div>
              <div class="form-group">
                <label for="exampleInputEmail1">Email </label>
                <input type="text" class="form-control" placeholder="Enter your Email"
                    
                    onChange={inputEvent}
                    value={data.email}
                    name="email"
               
                />
              </div>
 
 
  

            <button class="btn btn-primary my-2" type="submit">Click me</button>
            </form>


           


            </div>

        </div>
        
    </div>
            <div className="container contact_div" >
                 <div className="row" >

                <table border="1">
                <td align="center">
                    <th >Data</th>
                </td>
                </table>
                </div>
                </div>

        </>


);

}


export default Form;
